00097 void (playerDown)(struct Player *p){
00098     if(p->y_pos <= (arena.max_y - p->height - p->vel)){
00099         p->y_pos += p->vel;
00100     }
00101     else if (p->y_pos > (arena.max_y - p->height - p->vel))
00102     {
00103         p->y_pos += (arena.max_y - p->y_pos - p->height);
00104     }
00105 }
